# series = int(input("Please enter the series you'd like to check:\n (1) Live Series\n (2) Rookie\n (3) Breakout\n (4) Veteran\n (5) All-Star\n (6) Awards\n (7) Postseason\n (8) Future Stars\n (9) Signature\n (10) Topps Now\n (11) Finest\n (12) 2nd Half Heroes\n (13) Milestone\n (14) Captain\n (15) Charisma\n (16) World Baseball Classic\n"))

seriesOptions = ['Live Series', 'Rookie', 'Breakout', 'Veteran','All-Star','Awards','Postseason','Future Stars','Signature','Topps Now','Finest','2nd Half Heroes','Milestone','Captain','Charisma','World Baseball Classic','Sanford Greene','Home Run Derby','All-Star Game','Mexico City']



# ----------------------------------- Fundamentals/basics of the page -----------------------------------
st.set_page_config("MLB 23 Card Flipping Tool", "data:image/jpeg;base64,", 'wide')

st_autorefresh(40000, 1000)

# ----------------------------------- Streamlit sidebar elements -----------------------------------
with st.sidebar:

    st.title("Options:")

    profitWant = st.number_input("Minimum Profit: ", 100, 1000000, 250, step=50)

    seriesOption = st.selectbox("Series:", seriesOptions)



# ----------------------------------- Check which series to check profits for -----------------------------------
if seriesOption == 'Live Series':
    series = 1337
elif seriesOption == 'Rookie':
    series = 10001
elif seriesOption == 'Breakout':
    series = 10002
elif seriesOption == 'Veteran':
    series = 10003
elif seriesOption == 'All-Star':
    series = 10004
elif seriesOption == 'Awards':
    series = 10005
elif seriesOption == 'Postseason':
    series = 10006
elif seriesOption == 'Future Stars':
    series = 10008
elif seriesOption == 'Signature':
    series = 10009
elif seriesOption == 'Topps Now':
    series = 10017
elif seriesOption == 'Finest':
    series = 10018
elif seriesOption == '2nd Half Heroes':
    series = 10020
elif seriesOption == 'Milestone':
    series = 10022
elif seriesOption == 'Captain':
    series = 10026
elif seriesOption == 'Charisma':
    series = 10027
elif seriesOption == 'World Baseball Classic':
    series = 10028
elif seriesOption == 'Sanford Greene':
    series = 10025
elif seriesOption == 'Home Run Derby':
    series = 10030
elif seriesOption == 'All-Star Game':
    series = 10029
elif seriesOption == 'Mexico City':
    series = 10033
else:
    with st.sidebar:
        st.write("INVALID...CHECKING FOR LIVE SERIES.")
    series = 1337
    


# ----------------------------------- Actual code for checking the selected cards -----------------------------------
with st.spinner("Calculating..."):
    cardListings = requests.get(f"https://mlb23.theshow.com/apis/listings.json?type=mlb_card&series_id={series}")
    cardListings = cardListings.json()

    numPages = cardListings['total_pages']

    listingNames = []
    buyNowPrices = []
    sellNowPrices = []

    # Appends all needed card attributes within that series to the respective list
    for pageNumber in range(13):
        cardListings = requests.get(f"https://mlb23.theshow.com/apis/listings.json?type=mlb_card&series_id={series}&page={pageNumber+1}")
        cardListings = cardListings.json()
        for listing in cardListings['listings']:
            if not ((listing['best_sell_price'] == 0) or (listing['best_buy_price'] == 0)):
                buyNowPrices.append(listing['best_sell_price'])
                sellNowPrices.append(listing['best_buy_price'])
                listingNames.append(listing['listing_name'])

    # Initiate the table
    table = {}

    for i in range(len(listingNames)):
        profit = ((buyNowPrices[i] * 0.9) - sellNowPrices[i])
        if profit > profitWant:
            table[listingNames[i]] = [profit, buyNowPrices[i], sellNowPrices[i]]

    table = sorted(table.items(),key=lambda x:x[1], reverse=True) # Sorts table (which is a dictionary) in ascending order based on the values in the dictionary



# ----------------------------------- Code for displaying to main portion of the streamlit screen -----------------------------------
st.markdown("<h1 style='text-align: center;'>MLB 23 Flipping Tool</h1>", unsafe_allow_html=True)
st.markdown("Welcome to my card flipping tool!  This tool uses the MLB 23 API to determine realtime profits that can be made by players.  Donations are entirely optional, but greatly appreciated as I am a college student!")
st.markdown("* Cashapp - $TaiMeade")
st.markdown("* Venmo - @Stephan-Meade")

with st.expander("Notes:"):
    st.markdown("")
    st.markdown("* These numbers fluctuate heavily, but the refresh is done automatically every 40 seconds. Flip at your own risk!")
    st.markdown("* By default results are ordered in descending order, so the most profit you can make from a flip at that moment will be at the top.")
    st.markdown("* It is set to Live Series by default because those cards tend to move the fastest, thus making them the most effective for flipping.")
    st.markdown("")

st.markdown("---")

col1, col2, col3, col4 = st.columns(4)

# Names of players
with col1:
    st.subheader("Name:")
    for item in table:
        st.write(item[0])

# Profit to be made upon a flip
with col2:
    st.subheader("Profit:")
    for item in table:
        newItem = round(item[1][0],1)
        st.markdown(f"**{newItem}** :moneybag:")

# Current buy now price
with col3:
    st.subheader("Buy Now Price:")
    for item in table:
        st.markdown(item[1][1])

# Current sell now price
with col4:
    st.subheader("Sell Now Price:")
    for item in table:
        st.markdown(item[1][2])

st.markdown("---")